import React, { Fragment, lazy } from "react";
import { styled } from '@mui/material/styles';
import { deepPurple } from '@mui/material/colors';
const Badge = lazy(() => import('@mui/material/Badge'));
const Avatar = lazy(() => import('@mui/material/Avatar'));
const Typography = lazy(() => import('@mui/material/Typography'));
const Box = lazy(() => import("@mui/material/Box"));

const StyledBadge = styled(Badge)(({ theme }) => ({
  '& .MuiBadge-badge': {
    backgroundColor: '#44b700',
    color: '#44b700',
    boxShadow: `0 0 0 2px ${theme.palette.background.paper}`,
    '&::after': {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      borderRadius: '50%',
      animation: 'ripple 1.2s infinite ease-in-out',
      border: '1px solid currentColor',
      content: '""',
    },
  },
  '@keyframes ripple': {
    '0%': {
      transform: 'scale(.8)',
      opacity: 1,
    },
    '100%': {
      transform: 'scale(2.4)',
      opacity: 0,
    },
  },
}));

const Profile = ({ sidebarCollapse = false }) => {
  return (
    <Fragment>
      <StyledBadge
        overlap="circular"
        anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
        variant="dot"
      >
        <Avatar sx={{ bgcolor: deepPurple[500], width: 38, height: 38 }}>HP</Avatar>
      </StyledBadge>
      <Box sx={{ display: { xs: 'block', sm: !sidebarCollapse ? 'block' : 'none'} }}>
        <Typography variant="body2">
          Helmi Pratama
        </Typography>
        <p style={{ fontSize: "10px", margin: 0 }}>
          {`V ${process.env.REACT_APP_VERSION || "-.-"}`}
        </p>
      </Box>
    </Fragment>
  )
}

export default Profile;
